import React, { useContext } from 'react';
import { StateContext } from "../pages/sokpage";
import { Appstate } from '../types/domain';
import { Grid, Header, Message, Loader } from 'semantic-ui-react';

interface Props {
    tittel: string;
    erOkStatus: boolean;
    finnes: boolean;
    children?: Array<JSX.Element> | JSX.Element;
    orgnr: string;
}

function Kort(props: Props) {

    const color = props.erOkStatus ? "green" : "red";

    if (!props.finnes) {
        return (
            <Message color="grey">
                <Message.Header>{props.tittel}</Message.Header>
                <p>Fant ingen data for {props.orgnr}</p>
            </Message>
        )
    }

    if (!props.erOkStatus) {
        return (
            <Message color={color}>
                <Message.Header>{props.tittel}</Message.Header>
                {props.children}
            </Message>
        )
    }

    return (
        <Message color={color}>
            <Message.Header>{props.tittel}</Message.Header>
            {props.children}
        </Message>

    )
}

interface StatusProps {
    orgnr: string;
}

function Statuskort(props: StatusProps) {
    const state = useContext<Appstate>(StateContext);

    if (state.loading) {
        return <Loader active={state.loading} />
    }

    if (!state.submitted) {
        return null;
    }

    const { enhetsregisterResult, sentralGodkjenningResultat, arbeidstilsynetOrganisation } = state;

    return (
        <Grid>
            <Grid.Column width={6}>
                <Kort finnes={enhetsregisterResult ? true : false} orgnr={props.orgnr} tittel="MVA-register" erOkStatus={enhetsregisterResult && enhetsregisterResult.registrertIMvaregisteret ? true : false}>

                </Kort>
            </Grid.Column>
            <Grid.Column width={6}>
                <Kort finnes={sentralGodkjenningResultat ? true : false} orgnr={props.orgnr} tittel="Sentral godkjenning?" erOkStatus={sentralGodkjenningResultat && sentralGodkjenningResultat.status.approved ? true : false} />
            </Grid.Column>

            <Grid.Column width={6}>
                <Kort finnes={arbeidstilsynetOrganisation ? true : false} orgnr={props.orgnr} tittel="Registrert hos Arbeidstilsynet?" erOkStatus={arbeidstilsynetOrganisation && arbeidstilsynetOrganisation.Status ? true : false}>
                    <p>{arbeidstilsynetOrganisation && arbeidstilsynetOrganisation.Status}</p>
                </Kort>
            </Grid.Column>
        </Grid>
    )
}

export default Statuskort;
