import React, { useContext } from 'react';
import { StateContext } from "../pages/sokpage";
import { Appstate } from '../types/domain';
import { Grid, Header, Message, Loader } from 'semantic-ui-react';

interface Props {
    tittel: string;
    erOkStatus: boolean;
    children?: Array<JSX.Element> | JSX.Element;
}

function Kort(props: Props) {

    const color = props.erOkStatus ? "green" : "red";

    if (!props.erOkStatus) {
        return (
            <Message color={color}>
                <Message.Header>{props.tittel}</Message.Header>
                {props.children}
            </Message>
        )
    }

    return (
        <Message color={color}>
            <Message.Header>{props.tittel}</Message.Header>
            {props.children}
        </Message>

    )
}

function Statuskort() {
    const state = useContext<Appstate>(StateContext);

    if (state.loading) {
        return <Loader active={state.loading} />
    }

    if (!state.submitted) {
        return null;
    }

    const { enhetsregisterResult, sentralGodkjenningResultat, arbeidstilsynetOrganisation } = state;

    return (
        <Grid>
            <Grid.Column width={6}>
                <Kort tittel="Registrert i MVA-registeret?" erOkStatus={enhetsregisterResult && enhetsregisterResult.registrertIMvaregisteret ? true : false} />
            </Grid.Column>
            <Grid.Column width={6}>
                <Kort tittel="Finnes i Sentral godkjenning?" erOkStatus={sentralGodkjenningResultat && sentralGodkjenningResultat.status.approved ? true : false} />
            </Grid.Column>

            <Grid.Column width={6}>
                <Kort tittel="Registrert hos Arbeidstilsynet?" erOkStatus={arbeidstilsynetOrganisation && arbeidstilsynetOrganisation.Status ? true : false}>
                    <p>{arbeidstilsynetOrganisation && arbeidstilsynetOrganisation.Status}</p>
                </Kort>
            </Grid.Column>
        </Grid>
    )
}

export default Statuskort;
