import React, { useContext, useState } from 'react';
import { StateContext } from '../pages/sokpage';
import { Appstate } from '../types/domain';
import { Loader, Item, Segment } from 'semantic-ui-react';

function OppsummeringEnhetsregister() {
    const state = useContext<Appstate>(StateContext);

    if (state.loading) {
        return <Loader active={state.loading} />
    }

    if (!state.enhetsregisterResult) {
        return <p>Ingen data fra enhetsregister tilgjengelig...</p>;
    }

    const enhet = state.enhetsregisterResult;

    return (
        <Segment>
            <Item>
                <Item.Content>
                    <Item.Header>{enhet.navn} - {enhet.organisasjonsform.beskrivelse}</Item.Header>
                    <Item.Meta>Jobber med {enhet.naeringskode1.beskrivelse.toLowerCase()}</Item.Meta>
                    <Item.Extra>Ble stiftet {enhet.stiftelsesdato}</Item.Extra>
                </Item.Content>
            </Item>
        </Segment>
    )
}

export default OppsummeringEnhetsregister;
