import React, { useContext } from 'react';
import { StateContext } from "../pages/sokpage";
import { Appstate } from '../types/domain';
import { Grid, Header, Message, Loader } from 'semantic-ui-react';

interface Props {
    tittel: string;
    erOkStatus: boolean;
}

function Kort(props: Props) {

    const color = props.erOkStatus ? "green" : "red";

    if (!props.erOkStatus) {
        return (
            <Message color={color}>
                <Message.Header>{props.tittel}</Message.Header>
            </Message>
        )
    }

    return (
        <Message color={color}>
            <Message.Header>{props.tittel}</Message.Header>
        </Message>

    )
}

function Statuskort() {
    const state = useContext<Appstate>(StateContext);

    if (state.loading) {
        return <Loader active={state.loading} />
    }

    if (!state.submitted) {
        return null;
    }

    return (
        <Grid>
            <Grid.Column width={6}>
                <Kort tittel="Registrert i MVA-registeret?" erOkStatus={state.enhetsregisterResult && state.enhetsregisterResult.registrertIMvaregisteret ? true : false} />
            </Grid.Column>
            <Grid.Column width={6}></Grid.Column>
            <Grid.Column width={6}></Grid.Column>
        </Grid>
    )
}

export default Statuskort;
